create table seller (seller_id bigint generated by default as identity, address varchar(255), seller_name varchar(255), seller_type varchar(255), seller_url varchar(255), primary key (seller_id))
create table product (product_id bigint generated by default as identity, category varchar(255), description varchar(255), price double, product_name varchar(255), rating double, seller_seller_id bigint, primary key (product_id), CONSTRAINT seller_seller_id foreign key (seller_seller_id) references seller)
